  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates final price after applying membership discounts and booking time factors.
   * Algorithm:
   *   basePrice = this.price
   *   discount = 0
   *   IF customer.membershipPackage.hasAward(DISCOUNTS) THEN
   *     discount += 0.1  // 10% membership discount
   *   IF bookingTime is within 24 hours of departureTime THEN
   *     discount += 0.05  // 5% last-minute discount
   *   RETURN basePrice * (1 - discount)
   * Input:
   *   customer - Customer object for membership check
   *   bookingTime - String in format "YYYY-MM-DD HH:MM"
   * Output: double - final discounted price (range: 0 to original price)
   * Pre-condition: Trip price must be set, customer must have valid membership
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public double calculateDiscountedPrice(final Customer customer, final String bookingTime)
  {
    double basePrice = this.price;
    double discount = 0.0;
    
    // Check membership discount
    if (customer.getMembershipPackage().hasAward(Award.DISCOUNTS)) {
      discount += 0.1;
    }
    
    // Check last-minute discount (within 24 hours)
    // Parse bookingTime and departureTime to compare
    try {
      java.text.SimpleDateFormat sdf = new java.text.SimpleDateFormat("yyyy-MM-dd HH:mm");
      Date bookingDate = sdf.parse(bookingTime);
      Date departureDate = this.departureDate;
      
      long timeDifference = departureDate.getTime() - bookingDate.getTime();
      long hoursDifference = timeDifference / (60 * 60 * 1000);
      
      if (hoursDifference <= 24 && hoursDifference >= 0) {
        discount += 0.05;
      }
    } catch (Exception e) {
      // If parsing fails, no last-minute discount applied
    }
    
    return basePrice * (1 - discount);
  }

  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates reward points for this trip based on customer's membership.
   * Algorithm:
   *   points = 0
   *   IF customer.membershipPackage.hasAward(POINTS) THEN
   *     points = this.price * 0.1  // 1 point per $10 spent
   *   RETURN floor(points)
   * Input:
   *   customer - Customer object for membership check
   *   currentMonth - String in format "YYYY-MM" (for validation)
   * Output: integer - reward points earned (non-negative)
   * Pre-condition: Trip must be booked by customer in specified month
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public int calculateMonthlyPoints(final Customer customer, final String currentMonth)
  {
    double points = 0.0;
    
    // Check if customer has points award in membership package
    if (customer.getMembershipPackage().hasAward(Award.POINTS)) {
      points = this.price * 0.1;  // 1 point per $10 spent
    }
    
    return (int) Math.floor(points);
  }

