    // 1. Ensure flight is open for booking and now < flight.departureTime.
    if (!isOpenForBooking() || now.compareTo(getDepartureTime()) >= 0) {
      return false;
    }
    
    // 2. If stop is found in the flight's stopovers list, remove it.
    if (getStopovers().contains(stop)) {
      getStopovers().remove(stop);
      // 3. Return true; otherwise, return false.
      return true;
    }
    
