  public boolean createApplication(final Company company, final int shares, final double amount, final Document doc)
  {
    // Step 1: Validate shares > 0 and doc is not null.
    if (shares <= 0 || doc == null) {
      return false;
    }

    // Step 2: If customer.canApplyForIPO is false, return false.
    if (!isCanApplyForIPO()) {
      return false;
    }

    // Step 3: Check if any existing application for the same company is APPROVAL; if yes, return false.
    for (Application app : getApplications()) {
      if (app.getCompany() != null && app.getCompany().equals(company) && app.getStatus() == ApplicationStatus.APPROVAL) {
        return false;
      }
    }

    // Step 4: Create new Application instance and add it to customer's applications list.
    Application newApplication = IpoFactory.eINSTANCE.createApplication();
    newApplication.setCompany(company);
    newApplication.setShare(shares);
    newApplication.setAmountOfMoney(amount);
    newApplication.setAllowance(doc);
    newApplication.setCustomer(this);
    newApplication.setStatus(ApplicationStatus.PENDING); // As per post-condition

    getApplications().add(newApplication);

    // Step 5: Return true.
    return true;
  }

