  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates discounted price for a customer based on membership and booking time
   * Algorithm:
   *   basePrice = this.price
   *   discount = 0
   *   IF customer.membershipPackage.hasAward(DISCOUNTS)
   *     discount = discount + 0.1  // 10% discount for DISCOUNTS award
   *   IF bookingTime is early booking (e.g., >7 days before departure)
   *     discount = discount + 0.05  // 5% early booking discount
   *   discountedPrice = basePrice * (1 - discount)
   *   RETURN discountedPrice
   * Input: customer - Customer object, bookingTime - String in format "YYYY-MM-DD HH:MM"
   * Output: Double value representing discounted price
   * Pre-condition: Customer must have valid membership, bookingTime must be valid date
   * Post-condition: Returns calculated price without modifying trip state
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public double calculateDiscountedPrice(final Customer customer, final String bookingTime)
  {
    double basePrice = this.price;
    double discount = 0.0;
    
    // Check if customer has DISCOUNTS award
    if (customer.getMembershipPackage() != null && customer.getMembershipPackage().hasAward(edu.rideshare.Award.DISCOUNTS)) {
      discount += 0.1;
    }
    
    // Check for early booking (more than 7 days before departure)
    try {
      java.text.SimpleDateFormat sdf = new java.text.SimpleDateFormat("yyyy-MM-dd HH:mm");
      Date bookingDate = sdf.parse(bookingTime);
      long timeDifference = this.departureDate.getTime() - bookingDate.getTime();
      long daysDifference = timeDifference / (1000 * 60 * 60 * 24);
      if (daysDifference > 7) {
        discount += 0.05;
      }
    } catch (Exception e) {
      // If date parsing fails, continue without early booking discount
    }
    
    double discountedPrice = basePrice * (1 - discount);
    return discountedPrice;
  }

