  public int calculateMonthlyPoints(final Customer customer, final String currentMonth)
  {
    double basePoints = this.price * 0.1; // 1 point per $0.10 spent
    
    double points;
    if (customer.getMembershipPackage() != null && customer.getMembershipPackage().hasAward(edu.rideshare.Award.POINTS)) {
      points = basePoints * 1.5; // 50% bonus for POINTS award
    } else {
      points = basePoints;
    }
    
    return (int) points;
  }

