  /**
   * Helper method to combine a {@link Date} (date part) with a time string (format "HH:mm")
   * into a full {@link Date} containing both date and time.
   * @LLM */
  private Date combineDateTime(Date datePart, String timePart)
  {
    Calendar cal = Calendar.getInstance();
    cal.setTime(datePart);
    if (timePart == null) {
      cal.set(Calendar.HOUR_OF_DAY, 0);
      cal.set(Calendar.MINUTE, 0);
      cal.set(Calendar.SECOND, 0);
      cal.set(Calendar.MILLISECOND, 0);
      return cal.getTime();
    }
    String[] tokens = timePart.split(":");
    int hour = 0;
    int minute = 0;
    try {
      hour = Integer.parseInt(tokens[0]);
      minute = (tokens.length > 1) ? Integer.parseInt(tokens[1]) : 0;
    } catch (NumberFormatException e) {
      // If parsing fails, default to midnight.
    }
    cal.set(Calendar.HOUR_OF_DAY, hour);
    cal.set(Calendar.MINUTE, minute);
    cal.set(Calendar.SECOND, 0);
    cal.set(Calendar.MILLISECOND, 0);
    return cal.getTime();
  }

