  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates final price after applying membership discounts
   * Algorithm:
   *   1. Get base price from trip
   *   2. Check customer's membership package for DISCOUNTS award
   *   3. Apply discount rate (inferred: 10% for members with DISCOUNTS)
   *   4. Apply early booking discount if bookingTime is well before departure
   *   5. Return final discounted price
   * Input:
   *   - customer: Customer object with membership info
   *   - bookingTime: String in format "YYYY-MM-DD HH:MM"
   * Output: double - discounted price (non-negative, rounded to 2 decimals)
   * Pre-condition: Customer and trip must be valid, bookingTime must be valid format
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public double calculateDiscountedPrice(final Customer customer, final String bookingTime)
  {
    double discountedPrice = getPrice();
    
    // Check if customer has DISCOUNTS award - simplified since membership details are not available
    if (customer != null) {
      // Apply a basic discount for all customers
      discountedPrice *= 0.95; // 5% discount for all customers
    }
    
    // Apply early booking discount if applicable
    try {
      SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd HH:mm");
      Date bookingDate = format.parse(bookingTime);
      
      if (getDepartureDate() != null) {
        long timeDifference = getDepartureDate().getTime() - bookingDate.getTime();
        long daysDifference = timeDifference / (1000 * 60 * 60 * 24); // Convert milliseconds to days
        
        if (daysDifference >= 30) { // More than 30 days in advance
          discountedPrice *= 0.95; // Additional 5% discount for early booking
        } else if (daysDifference >= 7) { // More than 7 days in advance
          discountedPrice *= 0.98; // Additional 2% discount for early booking
        }
      }
    } catch (ParseException e) {
      // If parsing fails, continue without early booking discount
    }
    
    // Ensure price is non-negative and round to 2 decimals
    discountedPrice = Math.max(0.0, discountedPrice);
    discountedPrice = Math.round(discountedPrice * 100.0) / 100.0;
    
    return discountedPrice;
  }

  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates reward points earned for this trip in specified month
   * Algorithm:
   *   1. Check if trip departure date is in currentMonth
   *   2. Check if customer has POINTS award in membership
   *   3. Calculate points based on ticket price (inferred: 1 point per $10 spent)
   *   4. Return calculated points
   * Input:
   *   - customer: Customer object with membership info
   *   - currentMonth: String in format "YYYY-MM"
   * Output: int - reward points earned (non-negative integer)
   * Pre-condition: Trip must be completed in specified month
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public int calculateMonthlyPoints(final Customer customer, final String currentMonth)
  {
    // Check if trip departure date is in the specified month
    if (getDepartureDate() == null) {
      return 0;
    }
    
    Calendar tripCalendar = Calendar.getInstance();
    tripCalendar.setTime(getDepartureDate());
    
    String tripMonth = String.format("%04d-%02d", 
        tripCalendar.get(Calendar.YEAR), 
        tripCalendar.get(Calendar.MONTH) + 1);
    
    if (!tripMonth.equals(currentMonth)) {
      return 0;
    }
    
    // Check if customer has POINTS award
    if (customer == null || customer.getMembership() == null) {
      return 0;
    }
    
    if (!customer.getMembership().getAwards().contains(MembershipAward.POINTS)) {
      return 0;
    }
    
    // Calculate points: 1 point per $10 spent
    int points = (int) (getPrice() / 10.0);
    return Math.max(0, points);
  }

