  public boolean confirmBooking(final int seats)
  {
    if (seats <= 0) {
      return false;
    }
    
    if (seats <= getNumberOfSeats()) {
      setNumberOfSeats(getNumberOfSeats() - seats);
      return true;
    }
    
    return false;
  }

