  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates discounted price for customer based on membership and timing
   * Algorithm:
   *   1. Get base trip price
   *   2. Apply membership discount (e.g., 10% for premium members)
   *   3. Apply time-based discount if bookingTime meets criteria (e.g., early booking)
   *   4. Apply any additional awards from membership package
   *   5. Return final discounted price
   * Input:
   *   - customer: Customer object with membership package
   *   - bookingTime: String in format "YYYY-MM-DD HH:MM"
   * Output: double - discounted price (range: 0.0 - original price)
   * Pre-condition: Customer must have valid membership, trip must have valid price
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public double calculateDiscountedPrice(final Customer customer, final String bookingTime)
  {
    double discountedPrice = getPrice();
    
    // Apply membership discount
    if (customer != null && customer.getMembershipPackage() != null) {
      String packageType = customer.getMembershipPackage().toString();
      if (packageType.contains("PREMIUM")) {
        discountedPrice *= 0.9; // 10% discount for premium members
      }
    }
    
    // Apply time-based discount (early booking - more than 7 days in advance)
    try {
      SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd HH:mm");
      Date bookingDate = sdf.parse(bookingTime);
      Date departureDate = getDepartureDate();
      
      if (departureDate != null) {
        long diffInMillis = departureDate.getTime() - bookingDate.getTime();
        long diffInDays = diffInMillis / (1000 * 60 * 60 * 24);
        
        if (diffInDays > 7) {
          discountedPrice *= 0.95; // 5% discount for booking more than 7 days in advance
        }
      }
    } catch (ParseException e) {
      // If parsing fails, no time-based discount applied
    }
    
    // Apply additional awards from membership package
    if (customer != null && customer.getMembershipPackage() != null) {
      String packageType = customer.getMembershipPackage().toString();
      if (packageType.contains("AWARD")) {
        discountedPrice *= 0.98; // Additional 2% discount for award members
      }
    }
    
    // Ensure discounted price is not negative and not higher than original price
    discountedPrice = Math.max(0.0, Math.min(discountedPrice, getPrice()));
    
    return discountedPrice;
  }

  /**
   * <!-- begin-user-doc -->
   * <!-- end-user-doc -->
   * <!-- begin-model-doc -->
   * Summary: Calculates reward points earned from this trip for given month
   * Algorithm:
   *   1. Check if trip departure date is in current month
   *   2. Calculate base points based on trip price (e.g., 1 point per $10)
   *   3. Apply membership multiplier if customer has POINTS award
   *   4. Return calculated points
   * Input:
   *   - customer: Customer object with membership
   *   - currentMonth: String in format "YYYY-MM"
   * Output: integer - reward points for this trip (range: 0-1000)
   * Pre-condition: Trip must be completed in the specified month
   * Post-condition: None (read-only operation)
   * <!-- end-model-doc -->
   * @model annotation="http://www.ustb.edu.cn/sei/mde/EnhancedCodeGen generated='true'"
   * @generated NOT
   */
  public int calculateMonthlyPoints(final Customer customer, final String currentMonth)
  {
    int points = 0;
    
    // Check if trip departure date is in current month
    if (getDepartureDate() != null) {
      SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM");
      String tripMonth = sdf.format(getDepartureDate());
      
      if (tripMonth.equals(currentMonth)) {
        // Calculate base points (1 point per $10)
        points = (int) (getPrice() / 10);
        
        // Apply membership multiplier if customer has POINTS award
        if (customer != null && customer.getMembershipPackage() != null) {
          String packageType = customer.getMembershipPackage().toString();
          if (packageType.contains("POINTS")) {
            points *= 2; // Double points for POINTS package
          }
        }
      }
    }
    
    // Ensure points are within range 0-1000
    points = Math.max(0, Math.min(points, 1000));
    
    return points;
  }

